if("IntersectionObserver"in window){const e=new IntersectionObserver(r=>{r.forEach(t=>{t.isIntersecting&&(t.target.classList.add("is-visible"),e.unobserve(t.target))})},{threshold:.15});document.querySelectorAll("[data-reveal]").forEach(r=>e.observe(r))}else document.querySelectorAll("[data-reveal]").forEach(e=>e.classList.add("is-visible"));document.querySelectorAll('a[href^="#"]').forEach(e=>{e.addEventListener("click",r=>{const t=document.querySelector(e.getAttribute("href"));t&&(r.preventDefault(),t.scrollIntoView({behavior:"smooth"}))})});
